import streamlit as st
from app_home import app_run_home
from app_eda import app_run_eda
from app_diff import app_run_diff 
from streamlit_option_menu import option_menu

def main() :
    menu = ["프로젝트 소개","EDA (Exploratory Data Analysis)","다양한 통화의 환율을 비교 해보기"]

    with st.sidebar:
        st.markdown("\n")
        st.markdown("\n")

        st.image("data:image/jpeg;base64,", use_column_width=True)

        choose = option_menu("Menu", menu ,
                            icons=['bi bi-box', 'bi bi-bar-chart-line', 'bi bi-calculator-fill'],
                            menu_icon="bi bi-currency-exchange", default_index=0,
                            styles={
            "container": {"padding": "5!important", "background-color": "9999FF"},
            "icon": {"color": "red", "font-size": "23px"}, 
            "nav-link": {"font-size": "15px", "text-align": "left", "margin":"0px", "--hover-color": "gray"},
            "nav-link-selected": {"background-color": "gray"}
            }
        )

    if choose == menu[0] :
        app_run_home()
    elif choose == menu[1] :
        app_run_eda()
    elif choose == menu[2] :
        app_run_diff()            

if __name__ == "__main__" : main()